package foocafe.org.foocafe

import android.os.Bundle
import android.support.v7.app.AppCompatActivity
import android.support.v7.widget.Toolbar
import android.text.TextUtils
import android.util.Log
import android.view.View
import android.view.inputmethod.EditorInfo
import android.widget.AutoCompleteTextView
import android.widget.Button
import android.widget.EditText
import android.widget.TextView

import java.util.regex.Pattern

import foocafe.org.foocafe.entities.RegistrationCredential
import retrofit2.Call
import retrofit2.Callback
import retrofit2.Response
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory

class RegistrationActivity : AppCompatActivity() {
    private var foocafeAPI: FooCafeAPI? = null
    private var mEmailView: AutoCompleteTextView? = null
    private var mPasswordView: EditText? = null
    private var email: String? = null
    private var password: String? = null
    private var ab: android.support.v7.app.ActionBar? = null

    private var RegistrationsCallback: Callback<RegistrationCredential> = object : Callback<RegistrationCredential> {
        override fun onResponse(call: Call<RegistrationCredential>, response: Response<RegistrationCredential>) {
        }

        override fun onFailure(call: Call<RegistrationCredential>, t: Throwable) {
            t.printStackTrace()
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_registration)

        mEmailView = findViewById(R.id.email)
        mPasswordView = findViewById(R.id.password)

        val tool = findViewById<Toolbar>(R.id.my_toolbar)
        setSupportActionBar(tool)
        ab = supportActionBar

        val retrofit = Retrofit.Builder()
                .baseUrl(FooCafeAPI.ENDPOINT)
                .addConverterFactory(GsonConverterFactory.create())
                .build()
        foocafeAPI = retrofit.create(FooCafeAPI::class.java)

        mPasswordView!!.setOnEditorActionListener(TextView.OnEditorActionListener { textView, id, keyEvent ->
            if (id == R.id.login || id == EditorInfo.IME_NULL) {
                attemptRegister()
                return@OnEditorActionListener true
            }
            false
        })

        val button = findViewById<Button>(R.id.button)
        button.setOnClickListener {
            val regCred = RegistrationCredential(email!!, password!!)
            foocafeAPI!!.register(regCred).enqueue(RegistrationsCallback)
        }
    }

    private fun attemptRegister() {

        // Reset errors.
        mEmailView!!.error = null
        mPasswordView!!.error = null

        // Store values at the time of the register attempt.
        email = mEmailView!!.text.toString()
        password = mPasswordView!!.text.toString()

        var cancel = false
        var focusView: View? = null

        // Check for a valid password, if the user entered one.
        if (TextUtils.isEmpty(password)) {
            //  mPasswordView.setError("GG");
            focusView = mPasswordView
            cancel = true
        } else if (!TextUtils.isEmpty(password) && !isPasswordValid(password!!)) {
            mPasswordView!!.error = "Password invalid"
            focusView = mPasswordView
            cancel = true
        }

        // Check for a valid email address.
        if (TextUtils.isEmpty(email)) {
            mEmailView!!.error = getString(R.string.error_field_required)
            focusView = mEmailView
            cancel = true
        } else if (!isEmailValid(email!!)) {
            mEmailView!!.error = getString(R.string.error_invalid_email)
            focusView = mEmailView
            cancel = true
        }

        if (cancel) {
            // There was an error; don't attempt login and focus the first
            // form field with an error.
            focusView!!.requestFocus()
        } else {
            Log.i(TAG, "Email Valid")
            Log.i(TAG, "Password Valid")
            Log.i(TAG, "TODO check with database")
        }
    }

    private fun isEmailValid(email: String): Boolean {
        val matcher = VALID_EMAIL_REGEX.matcher(email)
        return matcher.find()
    }

    private fun isPasswordValid(password: String): Boolean {
        val matcher = VALID_PASSWORD_REGEX.matcher(password)
        return matcher.find()
    }

    companion object {

        private val TAG = "REGISTER"

